mapper.update:
UPDATE 
	hall_seats
SET
	seatStatus = #{seatStatus}
WHERE mateGroup = #{mateGroup}

UPDATE
	hall_seats
SET 
	hallId = #{hallId},
	seatRate = #{seatRate},
	seatStatus = #{seatStatus},
	mateGroup = #{mateGroup}
where
	seatRow = #{seatRow}
and	
	seatCol = #{seatCol}
and
	seatBlock = #{seatBlock}
AND 
	performanceId = #{performanceId}
