mapper.update:
update notice_main
set hits = hits + 1
where id = #{noticeId}

mapper.select:
select id, title, content, position
from notice_inside_content
where id = #{value}

select id, imagePath
from notice_image_path
where id = #{value}

mapper.update:
UPDATE notice_main
SET deleted = 'Y'
WHERE id = #{value}

update notice_main
set 
	title = #{mainTitle}
	, category = #{categoryNo}
	, badge = #{badge}
	, imoticon = #{mainEmoticon}
where 
	id = #{noticeId}

update notice_inside_content
set
	title =  #{TitleAndContent.title}
	, content = #{TitleAndContent.content}
where
	id = #{noticeId}
and
	position = #{TitleAndContent.position}
