mapper.select:
SELECT distinct A.id as id, A.title as title, A.category as category, A.provider as provider, A.rating as rating, A.runningTime as runningTime, A.startDate as startDate, A.endDate as endDate, A.point as point, A.likes as likes, A.imagePath as imagePath, A.`explain` as `explain`, C.name as hallName, C.address as hallAddress, A.reserveCount as reserveCount FROM performance_info A JOIN performance_main B ON B.infoId = A.id JOIN hall_info_main C ON C.id =A.hallInfoId JOIN performance_genre D ON A.id = D.id WHERE A.category= #{category}
and A.title like concat('%', #{title}, '%')
and D.genre IN
#{genre}
and A.startDate > #{startDate}
and #{endDate} > A.endDate
and endDate >= NOW()
and A.rating = #{age}
ORDER BY
A.startDate asc
A.likes desc, A.startDate asc
A.reserveCount desc, A.startDate asc
A.startDate asc

SELECT distinct A.id as id, A.title as title, A.category as category, A.provider as provider, A.rating as rating, A.runningTime as runningTime, A.startDate as startDate, A.endDate as endDate, A.point as point, A.likes as likes, A.imagePath as imagePath, A.`explain` as `explain`, C.name as hallName, C.address as hallAddress, A.reserveCount as reserveCount FROM performance_info A JOIN performance_main B ON B.infoId = A.id JOIN hall_info_main C ON C.id = A.hallInfoId JOIN performance_genre D ON A.id = D.id WHERE A.category= #{category}
and A.title like concat('%', #{title}, '%')
and D.genre IN
#{genre}
and A.startDate > #{startDate}
and #{endDate} > A.endDate
and A.endDate >= NOW()
and A.rating = #{age}
ORDER BY
A.startDate asc
A.likes desc, A.startDate asc
A.reserveCount desc, A.startDate asc
A.startDate asc
LIMIT #{endIndex} OFFSET #{beginIndex}

SELECT COUNT(*) AS cnt FROM ( SELECT distinct A.id as id, A.title as title, A.category as category, A.provider as provider, A.rating as rating, A.runningTime as runningTime, A.startDate as startDate, A.endDate as endDate, A.point as point, A.likes as likes, A.imagePath as imagePath, A.`explain` as `explain`, C.name as hallName, C.address as hallAddress, A.reserveCount as reserveCount FROM performance_info A JOIN performance_main B ON B.infoId = A.id JOIN hall_info_main C ON C.id =A.hallInfoId JOIN performance_genre D ON A.id = D.id WHERE A.category= #{category}
and A.title like concat('%', #{title}, '%')
and D.genre IN
#{genre}
and A.startDate > #{startDate}
and #{endDate} > A.endDate
and A.endDate >= NOW()
and A.rating = #{age}
ORDER BY A.startDate ) F

SELECT id, infoId, showDate, showTime, showNumber 
FROM performance_main
WHERE id = #{ performanceMainId}

mapper.delete:
DELETE FROM user_likes
WHERE
	id LIKE #{id}
AND
	infoId = #{performanceInfoId}

mapper.update:
UPDATE performance_info
SET 
	hallInfoId = #{hallInfo.id}
WHERE 
	id= #{id}

UPDATE performance_info
SET
	likes = #{likes}
WHERE
	id = #{id}

UPDATE performance_info
SET
	reserveCount = #{reserveCount}
WHERE
	id = #{id}
