mapper.insert:
update qna_main
set
	answerTitle = #{answerForm.answerTitle}
	, answerContent = #{answerForm.answerContent}
	, adminId = #{answerForm.adminId}
	, answerDate = now()
where 
	id = #{answerForm.qnaId}

mapper.update:
