mapper.select:
SELECT id as id, password, nickname, 
	   birthday, gender, email, tel, 
	   admin
FROM user_main 


		where id = #{value}


		where nickname = #{value}


		where email = #{value}


		where tel = #{value}

mapper.update:
UPDATE user_sub
	SET point = #{point}
WHERE id = #{id}

UPDATE user_sub
	SET deleted = 'Y'
WHERE id = #{id}

mapper.select:
select count(*)
from user_point_history
where userId = #{userId}

select count(*)
from user_coupon
where id = #{userId}
